/* JavaScript and TypeScript Primitives
The most basic types in TypeScript are called primitives.

These types form the building blocks of more complex types in your applications.

TypeScript includes all JavaScript primitives plus additional type features. */
//Boolean
let statu = true;
console.log(statu);
//Number
let num = 20;
console.log(num);
let dec = 2.134;
console.log(dec);
let hex = 0x243aed;
console.log(hex);
let bin = 0b1010;
console.log(bin);
let octal = 0o123;
console.log(octal);
//BigInt
let bigNum = 1927373n;
console.log(bigNum);
//String
let Fullname = "John Deo";
console.log(Fullname);
//symbol
const unq = Symbol("12ded");
console.log(unq);
